def zigzag(n):
    matrix = [[0]*n for _ in range(n)]
    count = 1
    row, col = 0, 0
    while count <= n*n:
        matrix[row][col] = count
        count += 1
        if row == 0 and col < n-1:
            col += 1
        elif row == n-1 and col > 0:
            row += 1
        elif row > 0 and col == n-1:
            row -= 1
        elif row < n-1 and col == 0:
            col += 1
        elif (row - col) % 2 == 0:
            row += 1
        else:
            col += 1
    return matrix

def print_matrix(matrix):
    for i in range(len(matrix)):
        for j in range(len(matrix[0])):
            print(f"{matrix[i][j]:3d}", end=" ")
        print()

def main():
    case = 1
    while True:
        n = int(input())
        if n == 0:
            break
        matrix = zigzag(n)
        print(f"Case {case}:")
        print_matrix(matrix)
        case += 1

if __name__ == "__main__":
    main()
