#include <iostream>
#include <vector>
#include <unordered_map>

int findColor(const std::vector<int>& arr) {
    int n = arr.size();
    if (n == 0) return -1;
    std::unordered_map<int, int> count;
    for (int num : arr) {
        if (count.find(num) != count.end()) {
            count[num]++;
        } else {
            count[num] = 1;
        }
    }
    for (const auto& pair : count) {
        if (pair.second > n / 2) {
            return pair.first;
        }
    }
    return -1;
}

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        int n;
        std::cin >> n;
        std::vector<int> arr(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> arr[i];
        }
        int color = findColor(arr);
        if (color == -1) {
            std::cout << "NO COLOR" << std::endl;
        } else {
            std::cout << color << std::endl;
        }
    }
    return 0;
}
