#include <iostream>
#include <cmath>

struct Point {
    double x, y;
};

double calculateDistance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p2.x - p1.x, 2) + std::pow(p2.y - p1.y, 2));
}

Point calculateCentroid(const Point& p1, const Point& p2, const Point& p3) {
    return { (p1.x + p2.x + p3.x) / 3, (p1.y + p2.y + p3.y) / 3 };
}

double calculateRadius(const Point& p1, const Point& p2, const Point& p3) {
    Point centroid = calculateCentroid(p1, p2, p3);
    return calculateDistance(p1, centroid);
}

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; i++) {
        Point p1, p2, p3;
        std::cin >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y;

        Point centroid = calculateCentroid(p1, p2, p3);
        double radius = calculateRadius(p1, p2, p3);

        std::cout << std::fixed << std::setprecision(3) << centroid.x << " " << centroid.y << " " << radius << std::endl;
    }

    return 0;
}
