import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class DominoesArrangement {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextLine()) {
            int n = Integer.parseInt(scanner.nextLine());
            String[] dominoes = scanner.nextLine().split(" ");
            Map<String, Integer> map = new HashMap<>();
            for (String domino : dominoes) {
                String key = Math.min(Integer.parseInt(domino.substring(0, 1)), Integer.parseInt(domino.substring(2, 3))) + ""
                        + Math.max(Integer.parseInt(domino.substring(0, 1)), Integer.parseInt(domino.substring(2, 3)));
                map.put(key, map.getOrDefault(key, 0) + 1);
            }
            boolean canBeArranged = true;
            for (int count : map.values()) {
                if (count % 2 != 0) {
                    canBeArranged = false;
                    break;
                }
            }
            System.out.println(canBeArranged ? "Yes" : "No");
        }
        scanner.close();
    }
}
