from collections import defaultdict

def can_arrange(dominoes):
    graph = defaultdict(list)
    for domino in dominoes:
        graph[domino[0]].append(domino[1])
        graph[domino[1]].append(domino[0])

    visited = set()
    for domino in dominoes:
        if domino[0] not in visited:
            if not dfs(graph, domino[0], visited):
                return False
    return True

def dfs(graph, node, visited):
    visited.add(node)
    for neighbor in graph[node]:
        if neighbor not in visited:
            if not dfs(graph, neighbor, visited):
                return False
    return True

def main():
    for _ in range(int(input())):
        n = int(input())
        dominoes = list(map(int, input().split()))
        if can_arrange(dominoes):
            print("Yes")
        else:
            print("No")

if __name__ == "__main__":
    main()
