import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // consume newline left-over

        for (int i = 0; i < n; i++) {
            double x1 = scanner.nextDouble();
            double y1 = scanner.nextDouble();
            double x2 = scanner.nextDouble();
            double y2 = scanner.nextDouble();
            double x3 = scanner.nextDouble();
            double y3 = scanner.nextDouble();

            double a = x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2);
            double b = (x1 * x1 + y1 * y1) * (y2 - y3) + (x2 * x2 + y2 * y2) * (y3 - y1) + (x3 * x3 + y3 * y3) * (y1 - y2);
            double c = (x1 * x1 + y1 * y1) * (x2 * y3 - x3 * y2) + (x2 * x2 + y2 * y2) * (x3 * y1 - x1 * y3) + (x3 * x3 + y3 * y3) * (x1 * y2 - x2 * y1);
            double d = 2 * (a * a + b * b);

            double px = (c - b * a * 2) / d;
            double py = (c + a * b * 2) / d;
            double r = Math.sqrt(a * a + b * b) / Math.sqrt(c * c + d);

            System.out.printf("%.3f %.3f %.3f%n", px, py, r);
        }
    }
}
