#include <iostream>
#include <vector>
#include <cmath>
#include <iomanip>

using namespace std;

struct Point {
    double x, y;
};

double crossProduct(const Point& p1, const Point& p2, const Point& p3) {
    return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
}

double dotProduct(const Point& p1, const Point& p2) {
    return p1.x * p2.x + p1.y * p2.y;
}

double norm(const Point& p) {
    return sqrt(dotProduct(p, p));
}

double angle(const Point& p1, const Point& p2) {
    return acos(dotProduct(p1, p2) / (norm(p1) * norm(p2)));
}

