#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAX_N 50
#define MAX_M 100
#define INF INT_MAX

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int T, s, d, i, j, k, x, y, z, min_dist, min_len;
    int dist[MAX_N][MAX_M], dist2[MAX_M][MAX_M];

    scanf("%d %d", &T, &s);
    while (T--) {
        scanf("%d %d", &d, &s);
        for (i = 0; i < s; i++)
            for (j = 0; j < d; j++) {
                scanf("%d", &x);
                if (x != 0)
                    dist[i][j] = x;
                else
                    dist[i][j] = INF;
            }
        for (i = 0; i < d - 1; i++)
            for (j = 0; j < d - i; j++) {
                scanf("%d", &x);
                if (j + i < d - 1)
                    scanf("%*d");
                if (x != 0)
                    dist2[i + j][i] = x;
                else
                    dist2[i + j][i] = INF;
            }
        min_len = 0;
        for (i = 0; i < s; i++)
            for (j = 0; j < d; j++)
                min_dist = INF;
        for (i = 0; i < s; i++)
            for (j = 0; j < d; j++)
                for (k = 0; k < d; k++)
                    min_dist = min(min_dist, dist[i][j] + dist2[j][k]);
        for (i = 0; i < s; i++)
            for (j = 0; j < d; j++)
                min_dist = min(min_dist, dist[i][j] + dist2[j][d - 1]);
        for (i = 0; i < d - 1; i++)
            for (j = 0; j < d - i; j++)
                min_dist = min(min_dist, dist2[i + j][i] + dist2[i + j][d - 1]);
        min_len += min_dist;
        printf("%d\n", min_len);
    }
    return 0;
}
