#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAX_N = 50;
const int MAX_M = 50;
const int MAX_D = 100;

int main() {
    int T;
    cin >> T;
    while (T--) {
        int s, m;
        cin >> s >> m;
        vector<vector<int>> dist(s, vector<int>(m));
        for (int i = 0; i < s; i++) {
            for (int j = 0; j < m; j++) {
                cin >> dist[i][j];
            }
        }
        for (int i = 0; i < m - 1; i++) {
            for (int j = 0; j < m - i - 1; j++) {
                cin >> dist[i][j];
            }
        }
        vector<vector<int>> adj(m);
        for (int i = 0; i < s; i++) {
            for (int j = 0; j < m; j++) {
                if (dist[i][j] != 0) {
                    adj[j].push_back(i);
                }
            }
        }
        vector<int> minDist(m, numeric_limits<int>::max());
        minDist[0] = 0;
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
        pq.push({0, 0});
        while (!pq.empty()) {
            int u = pq.top().second;
            pq.pop();
            for (int v : adj[u]) {
                int newDist = minDist[u] + dist[v][u];
                if (newDist < minDist[v]) {
                    minDist[v] = newDist;
                    pq.push({newDist, v});
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < m; i++) {
            ans += minDist[i];
        }
        cout << ans << endl;
    }
    return 0;
}
