import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {
    static class Edge {
        int to, weight;

        public Edge(int to, int weight) {
            this.to = to;
            this.weight = weight;
        }
    }

    static class Graph {
        int n, m;
        int[] head;
        Edge[] e;

        public Graph(int n) {
            this.n = n;
            this.m = 0;
            head = new int[n + 1];
            Arrays.fill(head, -1);
            e = new Edge[2 * n + 1];
        }

        public void addEdge(int u, int v, int w) {
            e[++m] = new Edge(v, w);
            e[m].next = head[u];
            head[u] = m;
        }
    }

    static int INF = 0x3f3f3f3f;
    static int[] dis;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] line;
        while (true) {
            line = br.readLine().split("\\s+");
            if (line.length == 2 && line[0].equals("0") && line[1].equals("0")) break;
            int s = Integer.parseInt(line[0]);
            int d = Integer.parseInt(line[1]);
            Graph g = new Graph(s + d);
            for (int i = 1; i <= s; i++) {
                line = br.readLine().split("\\s+");
                for (int j = 1; j <= d; j++) {
                    int w = Integer.parseInt(line[j - 1]);
                    if (w != 0) g.addEdge(i, j + s, w);
                }
            }
            for (int i = 1; i <= d - 1; i++) {
                line = br.readLine().split("\\s+");
                for (int j = 1; j <= d - i; j++) {
                    int w = Integer.parseInt(line[j - 1]);
                    if (w != 0) g.addEdge(i + s, i + j + s, w);
                }
            }
            dis = new int[g.n + 1];
            Arrays.fill(dis, INF);
            dis[1] = 0;
            for (int i = 1; i <= g.n; i++) {
                for (int j = 1; j <= g.m; j++) {
                    Edge e = g.e[j];
                    if (dis[e.to] > dis[g.e[j - 1].to] + e.weight) dis[e.to] = dis[g.e[j - 1].to] + e.weight;
                }
            }
            int ans = 0;
            for (int i = 1; i <= g.n; i++) ans += dis[i];
            System.out.println(ans);
        }
    }
}
