import sys
import heapq

def read_input():
    lines = sys.stdin.readlines()
    s, d = map(int, lines[0].split())
    graph = []
    for _ in range(s):
        graph.append(list(map(int, lines[_+1].split())))
    for _ in range(d-1):
        graph.append(list(map(int, lines[s+1+_].split())))
    return s, d, graph

def compute_min_length(s, d, graph):
    min_length = 0
    queue = []
    for i in range(s):
        for j in range(d):
            if graph[i][j] != 0:
                heapq.heappush(queue, (graph[i][j], i, j))
    visited = [[False]*d for _ in range(s)]
    while queue:
        length, source, target = heapq.heappop(queue)
        if not visited[source][target]:
            min_length += length
            visited[source][target] = True
            for i in range(d):
                if graph[target][i] != 0 and not visited[target][i]:
                    heapq.heappush(queue, (graph[target][i], target, i))
    return min_length

def main():
    s, d, graph = read_input()
    while s != 0 and d != 0:
        print(compute_min_length(s, d, graph))
        s, d, graph = read_input()

if __name__ == "__main__":
    main()
