#include <stdio.h>
#include <stdbool.h>

#define MAX_NODES 30
#define MAX_EDGES 50

bool visited[MAX_NODES];
int min_dom_set[MAX_NODES];
int min_size = MAX_NODES;

void dfs(int node, int graph[MAX_NODES][MAX_NODES], int n) {
    visited[node] = true;
    for (int i = 0; i < n; i++) {
        if (graph[node][i] && !visited[i]) {
            dfs(i, graph, n);
        }
    }
}

void find_min_dom_set(int graph[MAX_NODES][MAX_NODES], int n) {
    for (int i = 0; i < n; i++) {
        if (!visited[i]) {
            min_dom_set[i] = 1;
            dfs(i, graph, n);
            int size = 0;
            for (int j = 0; j < n; j++) {
                if (min_dom_set[j]) {
                    size++;
                }
            }
            if (size < min_size) {
                min_size = size;
            }
            for (int j = 0; j < n; j++) {
                min_dom_set[j] = 0;
            }
            min_size = MAX_NODES;
        }
    }
}

int main() {
    int n, m;
    while (scanf("%d %d", &n, &m) == 2) {
        int graph[MAX_NODES][MAX_NODES] = {{0}};
        for (int i = 0; i < m; i++) {
            int u, v;
            scanf("%d %d", &u, &v);
            graph[u][v] = graph[v][u] = 1;
        }
        for (int i = 0; i < n; i++) {
            visited[i] = false;
        }
        find_min_dom_set(graph, n);
        printf("%d\n", min_size);
        scanf("%d %d", &n, &m);
        if (n == 0 && m == 0) {
            break;
        }
    }
    return 0;
}
