#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAX_N = 30;

bool visited[MAX_N];
vector<int> adj[MAX_N];

void dfs(int node) {
    visited[node] = true;
    for (int neighbor : adj[node]) {
        if (!visited[neighbor]) {
            dfs(neighbor);
        }
    }
}

int minDominatingSet(int n, int m) {
    for (int i = 0; i < n; i++) {
        visited[i] = false;
    }

    int dominatingSetSize = 0;
    for (int i = 0; i < n; i++) {
        if (!visited[i]) {
            dfs(i);
            dominatingSetSize++;
        }
    }

    return dominatingSetSize;
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) {
            break;
        }

        for (int i = 0; i < n; i++) {
            adj[i].clear();
        }

        for (int i = 0; i < m; i++) {
            int u, v;
            cin >> u >> v;
            adj[u].push_back(v);
            adj[v].push_back(u);
        }

        cout << minDominatingSet(n, m) << endl;
    }

    return 0;
}
