from collections import deque

def min_dominating_set(n, edges):
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * n
    dominating_set = set()

    for i in range(n):
        if not visited[i]:
            queue = deque([i])
            visited[i] = True
            dominating_set.add(i)

            while queue:
                u = queue.popleft()
                for v in graph[u]:
                    if not visited[v]:
                        visited[v] = True
                        dominating_set.add(v)
                        queue.append(v)

    return len(dominating_set)


def main():
    while True:
        n, m = map(int, input().split())
        if n == 0 and m == 0:
            break
        edges = []
        for _ in range(m):
            u, v = map(int, input().split())
            edges.append((u, v))
        print(min_dominating_set(n, edges))


if __name__ == "__main__":
    main()
