#include <stdio.h>
#include <stdlib.h>

#define N 1001

int f(int i) {
    if (i <= 1) return 1;
    return f(i - 1) + f(i - 2);
}

int main() {
    int v, d, i, j, count, connected;
    int *F;

    while (scanf("%d %d", &v, &d) == 2) {
        F = (int *)malloc((v + 1) * sizeof(int));
        F[0] = 1;
        F[1] = 1;

        for (i = 2; i <= v; i++) {
            F[i] = (f(i) % N);
        }

        count = 0;
        connected = 0;

        for (i = 0; i < v; i++) {
            for (j = i + 1; j < v; j++) {
                if (abs(F[i] - F[j]) < d) {
                    connected = 1;
                }
            }
            if (connected) {
                count++;
                connected = 0;
            }
        }

        printf("%d\n", count + 1);
        free(F);
    }

    return 0;
}
