#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1001;

int main() {
    int V, d;
    while (cin >> V >> d) {
        vector<int> fib(V + 1);
        fib[0] = 1;
        for (int i = 1; i <= V; i++) {
            fib[i] = (fib[i - 1] + fib[i - 2]) % N;
        }

        vector<int> cnt(N);
        for (int i = 1; i <= V; i++) {
            cnt[fib[i]]++;
        }

        int ans = 0;
        for (int i = 0; i < N; i++) {
            if (cnt[i] > 0) {
                int j = (i + d) % N;
                while (cnt[j] > 0) {
                    ans += cnt[i] * cnt[j];
                    j = (j + d) % N;
                }
                ans += cnt[i] * (cnt[i] - 1) / 2;
            }
        }

        cout << ans << endl;
    }

    return 0;
}
