import sys

def fibonacci(n, mod):
    fib = [0, 1]
    for i in range(2, n + 1):
        fib.append((fib[i - 1] + fib[i - 2]) % mod)
    return fib

def count_connected_subsets(v, d, mod):
    fib = fibonacci(v, mod)
    graph = [[] for _ in range(v + 1)]
    for i in range(1, v + 1):
        for j in range(i + 1, v + 1):
            if abs(fib[i] - fib[j]) < d:
                graph[i].append(j)
                graph[j].append(i)
    visited = [False] * (v + 1)
    count = 0
    for i in range(1, v + 1):
        if not visited[i]:
            count += 1
            stack = [i]
            visited[i] = True
            while stack:
                node = stack.pop()
                for neighbor in graph[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        stack.append(neighbor)
    return count

def main():
    mod = 1001
    for _ in range(int(sys.stdin.readline())):
        v, d = map(int, sys.stdin.readline().split())
        print(count_connected_subsets(v, d, mod))

if __name__ == "__main__":
    main()
