#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, r, c, i, j, k, top;
    int *deck, *deckA, *deckB, *deckC;

    while (scanf("%d %d", &n, &r) == 2) {
        deck = (int *)malloc((n + 1) * sizeof(int));
        deckA = (int *)malloc((n + 1) * sizeof(int));
        deckB = (int *)malloc((n + 1) * sizeof(int));
        deckC = (int *)malloc((n + 1) * sizeof(int));

        for (i = 0; i <= n; i++) {
            deck[i] = i;
        }

        for (i = 0; i < r; i++) {
            scanf("%d", &c);
            for (j = n; j >= 0; j--) {
                if (j >= c) {
                    deckA[j] = deck[j - c];
                } else {
                    deckA[j] = deck[j];
                }
            }

            for (j = 0; j < n; j++) {
                deckB[j] = deck[j];
            }

            for (j = 0; j < n; j++) {
                deckC[j] = 0;
            }

            k = 0;
            for (j = 0; j < n; j++) {
                if (deckA[j] != 0) {
                    deckC[k++] = deckA[j];
                }
                if (deckB[j] != 0) {
                    deckC[k++] = deckB[j];
                }
            }

            for (j = 0; j < n; j++) {
                deck[j] = deckC[j];
            }

            for (j = 0; j < n; j++) {
                deckA[j] = 0;
                deckB[j] = 0;
            }
        }

        top = deck[0];
        printf("%d\n", top);

        free(deck);
        free(deckA);
        free(deckB);
        free(deckC);
    }

    return 0;
}
