#include <iostream>
#include <vector>

int riffleShuffle(int n, int r, std::vector<int> c) {
    std::vector<int> deck(n);
    for (int i = 0; i < n; i++) {
        deck[i] = n - i - 1;
    }

    for (int i = 0; i < r; i++) {
        int c_val = c[i];
        std::vector<int> deckA, deckB;
        for (int j = 0; j < n; j++) {
            if (j < n / 2) {
                deckA.push_back(deck[j]);
            } else {
                deckB.push_back(deck[j]);
            }
        }

        std::vector<int> deckC;
        while (deckA.size() >= c_val) {
            for (int j = 0; j < c_val; j++) {
                deckC.push_back(deckA.back());
                deckA.pop_back();
            }
            for (int j = 0; j < c_val; j++) {
                deckC.push_back(deckB.back());
                deckB.pop_back();
            }
        }

        if (!deckA.empty()) {
            deckC.insert(deckC.end(), deckA.begin(), deckA.end());
        }
        if (!deckB.empty()) {
            deckC.insert(deckC.end(), deckB.begin(), deckB.end());
        }

        deck = deckC;
    }

    return deck[0];
}

int main() {
    int n, r;
    while (std::cin >> n >> r) {
        std::vector<int> c(r);
        for (int i = 0; i < r; i++) {
            std::cin >> c[i];
        }
        std::cout << riffleShuffle(n, r, c) << std::endl;
    }
    return 0;
}
