import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNext()) {
            int n = scanner.nextInt();
            int r = scanner.nextInt();
            int[] cards = new int[n];
            for (int i = 0; i < n; i++) {
                cards[i] = i;
            }
            for (int i = 0; i < r; i++) {
                int c = scanner.nextInt();
                int[] deckA = new int[(n + 1) / 2];
                int[] deckB = new int[n / 2];
                System.arraycopy(cards, 0, deckA, 0, (n + 1) / 2);
                System.arraycopy(cards, (n + 1) / 2, deckB, 0, n / 2);
                int j = 0;
                while (j < deckA.length || j < deckB.length) {
                    for (int k = 0; k < c && j < deckA.length; k++) {
                        cards[n - j - 1] = deckA[deckA.length - j - 1];
                    }
                    for (int k = 0; k < c && j < deckB.length; k++) {
                        cards[n - j - 1] = deckB[deckB.length - j - 1];
                    }
                    j++;
                }
            }
            System.out.println(cards[0]);
        }
    }
}
