#include <stdio.h>
#include <stdlib.h>

#define MAX_N 110

int main() {
    int n, i, j, p[MAX_N], dp[MAX_N][MAX_N], min[MAX_N][MAX_N];

    while (scanf("%d", &n) == 1) {
        for (i = 1; i <= n; i++) {
            scanf("%d", &p[i]);
        }

        for (i = 1; i <= n; i++) {
            for (j = 1; j <= n; j++) {
                dp[i][j] = 0;
                min[i][j] = 0;
            }
        }

        for (i = 1; i <= n; i++) {
            for (j = 1; j <= i; j++) {
                for (int k = j; k < i; k++) {
                    dp[i][j] = dp[i][j] > dp[i - 1][k] + p[k] ? dp[i][j] : dp[i - 1][k] + p[k];
                    min[i][j] = min[i][j] > min[i - 1][k] + p[k] ? min[i][j] : min[i - 1][k] + p[k];
                }
                dp[i][i] = min[i][i] = dp[i - 1][i - 1] + p[i];
            }
        }

        int ans = 0;
        for (i = 1; i <= n; i++) {
            ans += min[n][i];
        }

        printf("%d\n", ans + 20 * (n - 1));
    }

    return 0;
}
