#include <stdio.h>

int main() {
    int n, k, i, j;
    int fridge[101][101];
    int vampire[101][101];

    while (scanf("%d %d", &n, &k) == 2) {
        if (n == 0 && k == 0) {
            break;
        }

        for (i = 0; i < k; i++) {
            scanf("%d", &fridge[0][i]);
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < k; j++) {
                scanf("%d", &vampire[i][j]);
            }
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < k; j++) {
                fridge[0][j] -= vampire[i][j];
            }
        }

        int flag = 1;
        for (i = 0; i < k; i++) {
            if (fridge[0][i] < 0) {
                flag = 0;
                break;
            }
        }

        if (flag) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    }

    return 0;
}
