#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    std::string line;
    std::getline(std::cin, line); // ignore the first line
    while (std::getline(std::cin, line)) {
        if (line.empty()) break;
        std::istringstream iss(line);
        int n, k;
        iss >> n >> k;
        std::vector<int> fridge(k);
        for (int i = 0; i < k; ++i) {
            iss >> fridge[i];
        }
        std::vector<std::vector<int>> vampires(n, std::vector<int>(k));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < k; ++j) {
                iss >> vampires[i][j];
            }
        }
        bool canPrepare = true;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < k; ++j) {
                if (vampires[i][j] > fridge[j]) {
                    canPrepare = false;
                    break;
                }
                fridge[j] -= vampires[i][j];
            }
            if (!canPrepare) break;
        }
        std::cout << (canPrepare ? "Yes" : "No") << std::endl;
    }
    return 0;
}
