#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int w;
    std::cin >> w;

    std::vector<int> nums(w + 1);
    for (int i = 1; i <= w; ++i) {
        nums[i] = i;
    }

    int n;
    std::cin >> n;

    for (int i = 0; i < n; ++i) {
        int a, b;
        std::cin >> a >> b;

        std::swap(nums[a], nums[b]);
    }

    for (int i = 1; i <= w; ++i) {
        std::cout << nums[i] << " ";
    }

    return 0;
}
