import sys

def dfs(x, y, n, s, t, b, vis, prob):
    if n == 0:
        return 1 if x == t else 0
    if vis[x][y][n] == 1:
        return prob[x][y][n]
    vis[x][y][n] = 1
    prob[x][y][n] = 0
    for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < 3 and 0 <= ny < 3 and (nx, ny) != (b // 3, b % 3):
            prob[x][y][n] += dfs(nx, ny, n - 1, s, t, b, vis, prob) / 4
    return prob[x][y][n]

def solve():
    for _ in range(int(input())):
        n = int(input())
        s, t, b = input().split()
        s, t, b = ord(s) - ord('A'), ord(t) - ord('A'), ord(b) - ord('A')
        vis = [[[0] * 16 for _ in range(3)] for _ in range(3)]
        prob = [[[0.0] * 16 for _ in range(3)] for _ in range(3)]
        print(dfs(s // 3, s % 3, n, s, t, b, vis, prob))

if __name__ == "__main__":
    solve()
