#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    std::vector<std::string> lines;
    std::string line;
    while (std::getline(std::cin, line)) {
        if (line == "END_OF_TEXT") break;
        lines.push_back(line);
    }

    std::vector<std::string> commands;
    std::string command;
    std::getline(std::cin, command);
    while (command != "-") {
        commands.push_back(command);
        std::getline(std::cin, command);
    }

    int cursorLine = 0;
    int cursorPos = 0;
    std::string buffer;
    std::vector<std::string> editedLines;

    for (int i = 0; i < lines.size(); i++) {
        editedLines.push_back("");
        for (int j = 0; j < lines[i].size(); j++) {
            editedLines[i] += lines[i][j];
        }
    }

    for (int i = 0; i < commands.size(); i++) {
        char c = commands[i][0];
        switch (c) {
            case 'a':
                cursorPos = 0;
                break;
            case 'e':
                cursorPos = editedLines[cursorLine].size();
                break;
            case 'p':
                if (cursorLine > 0) {
                    cursorLine--;
                    cursorPos = 0;
                }
                break;
            case 'n':
                if (cursorLine < editedLines.size() - 1) {
                    cursorLine++;
                    cursorPos = 0;
                }
                break;
            case 'f':
                if (cursorPos < editedLines[cursorLine].size()) {
                    cursorPos++;
                } else if (cursorLine < editedLines.size() - 1) {
                    cursorLine++;
                    cursorPos = 0;
                }
                break;
            case 'b':
                if (cursorPos > 0) {
                    cursorPos--;
                } else if (cursorLine > 0) {
                    cursorLine--;
                    cursorPos = editedLines[cursorLine].size();
                }
                break;
            case 'd':
                if (cursorPos < editedLines[cursorLine].size()) {
                    editedLines[cursorLine].erase(cursorPos, 1);
                    if (cursorPos < editedLines[cursorLine].size()) {
                        cursorPos++;
                    }
                } else if (cursorLine < editedLines.size() - 1) {
                    editedLines[cursorLine] += editedLines[cursorLine + 1];
                    editedLines.erase(editedLines.begin() + cursorLine + 1);
                    cursorLine++;
                    cursorPos = editedLines[cursorLine].size();
                }
                break;
            case 'k':
                if (cursorPos == editedLines[cursorLine].size() && cursorLine < editedLines.size() - 1) {
                    editedLines[cursorLine] += editedLines[cursorLine + 1];
                    editedLines.erase(editedLines.begin() + cursorLine + 1);
                    cursorLine++;
                    cursorPos = editedLines[cursorLine].size();
                    buffer += "\n";
                } else if (cursorPos < editedLines[cursorLine].size()) {
                    buffer += editedLines[cursorLine].substr(cursorPos);
                    editedLines[cursorLine].erase(cursorPos, buffer.size());
                    cursorPos = editedLines[cursorLine].size();
                }
                break;
            case 'y':
                if (buffer.empty()) {
                    break;
                } else {
                    if (buffer[0] == '\n') {
                        editedLines.insert(editedLines.begin() + cursorLine, buffer);
                        cursorLine++;
                        cursorPos = 0;
                        buffer.clear();
                    } else {
                        editedLines[cursorLine].insert(cursorPos, buffer);
                        cursorPos += buffer.size();
                        buffer.clear();
                    }
                }
                break;
        }
    }

    for (int i = 0; i < editedLines.size(); i++) {
        std::cout << editedLines[i] << std::endl;
    }

    return 0;
}
