from itertools import permutations

def is_valid(eq):
    try:
        return eval(eq.replace('/', ' / ').replace('*', ' *').replace('-', ' -').replace('+', ' +'))
    except ZeroDivisionError:
        return False
    except Exception:
        return False

def is_valid_puzzle(grid, ops):
    n = len(grid)
    for i in range(n):
        for j in range(n):
            if grid[i][j] == '.':
                for op in ops:
                    grid[i][j] = op
                    if is_valid(''.join(grid[i]) + '=' + ''.join(grid[i+1:])):
                        return True
                    grid[i][j] = '.'
    return False

def solve_puzzle(grid, ops):
    n = len(grid)
    for i in range(n):
        for j in range(n):
            if grid[i][j] == '.':
                for op in ops:
                    grid[i][j] = op
                    if is_valid_puzzle(grid, ops):
                        return True
                    grid[i][j] = '.'
    return False

def main():
    while True:
        try:
            h, w = map(int, input().split())
            grid = [list(input().strip()) for _ in range(h)]
            n = int(input())
            ops = list(input().strip())
            if solve_puzzle(grid, ops):
                print("Yes")
            else:
                print("No")
        except ValueError:
            break

if __name__ == "__main__":
    main()
