#include <stdio.h>
#include <stdlib.h>

int compare(const void *a, const void *b) {
    int *x = (int *)a;
    int *y = (int *)b;
    return (*x - *y);
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        int N, M, R;
        scanf("%d %d %d", &N, &M, &R);
        int X[N], Y[N], X1[M], Y1[M];
        for (int i = 0; i < N; i++) {
            scanf("%d %d", &X[i], &Y[i]);
        }
        for (int i = 0; i < M; i++) {
            scanf("%d %d", &X1[i], &Y1[i]);
        }
        qsort(X, N, sizeof(int), compare);
        qsort(Y, N, sizeof(int), compare);
        qsort(X1, M, sizeof(int), compare);
        qsort(Y1, M, sizeof(int), compare);
        int i = 0, j = 0, ans = 0;
        while (i < N && j < M) {
            if (X[i] - X1[j] <= 2 * R && Y[i] - Y1[j] <= 2 * R) {
                ans++;
                j++;
            } else {
                i++;
            }
        }
        printf("%d\n", ans);
    }
    return 0;
}
