#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int countPoints(int x, int y, int R, vector<pair<int, int>> &points) {
    int count = 0;
    for (auto &point : points) {
        int dx = abs(x - point.first);
        int dy = abs(y - point.second);
        if (dx * dx + dy * dy <= 4 * R * R) {
            count++;
        }
    }
    return count;
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        int N, M, R;
        cin >> N >> M >> R;
        vector<pair<int, int>> points1(N), points2(M);
        for (int i = 0; i < N; i++) {
            cin >> points1[i].first >> points1[i].second;
        }
        for (int i = 0; i < M; i++) {
            cin >> points2[i].first >> points2[i].second;
        }
        int total = 0;
        for (auto &point : points1) {
            total += countPoints(point.first, point.second, R, points2);
        }
        cout << total << endl;
    }
    return 0;
}
