import sys

def count_points(x, y, r, points):
    count = 0
    for point in points:
        if (x - point[0])**2 + (y - point[1])**2 <= (2*r)**2:
            count += 1
    return count

def main():
    for line in sys.stdin:
        line = line.strip()
        if not line:
            continue
        if line == "0 0":
            break
        an, bn, r = map(int, line.split())
        planes = []
        bullets = []
        for _ in range(an):
            x, y = map(int, sys.stdin.readline().strip().split())
            planes.append((x, y))
        for _ in range(bn):
            x, y = map(int, sys.stdin.readline().strip().split())
            bullets.append((x, y))
        total_power = 0
        for plane in planes:
            total_power += count_points(plane[0], plane[1], r, bullets)
        print(total_power)

if __name__ == "__main__":
    main()
