#include <stdio.h>
#include <string.h>

#define N 64
#define K 262

char grid[N][N];
int n, k;

void print_grid() {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            printf("%c", grid[i][j]);
        }
        printf("\n");
    }
}

void solve(int i, int j, int dir, int color) {
    if (i < 0 || i >= n || j < 0 || j >= n) return;
    if (grid[i][j] != '.') {
        if (grid[i][j] != color) return;
        if (dir == 0) {
            grid[i][j] = 'E';
            solve(i - 1, j, 0, color);
            solve(i, j + 1, 1, color);
            solve(i + 1, j, 2, color);
            solve(i, j - 1, 3, color);
        } else if (dir == 1) {
            grid[i][j] = 'E';
            solve(i, j - 1, 3, color);
            solve(i + 1, j, 2, color);
            solve(i, j + 1, 1, color);
            solve(i - 1, j, 0, color);
        } else if (dir == 2) {
            grid[i][j] = 'E';
            solve(i + 1, j, 2, color);
            solve(i, j + 1, 1, color);
            solve(i - 1, j, 0, color);
            solve(i, j - 1, 3, color);
        } else {
            grid[i][j] = 'E';
            solve(i - 1, j, 0, color);
            solve(i, j - 1, 3, color);
            solve(i + 1, j, 2, color);
            solve(i, j + 1, 1, color);
        }
    }
}

void dfs(int i, int j) {
    if (i < 0 || i >= n || j < 0 || j >= n) return;
    if (grid[i][j] == 'E') {
        grid[i][j] = '.';
        dfs(i - 1, j);
        dfs(i, j - 1);
        dfs(i + 1, j);
        dfs(i, j + 1);
    }
}

void generate(int i, int j, int color) {
    if (i < 0 || i >= n || j < 0 || j >= n) return;
    if (grid[i][j] == '.') {
        grid[i][j] = color;
        generate(i - 1, j, color);
        generate(i, j - 1, color);
        generate(i + 1, j, color);
        generate(i, j + 1, color);
    }
}

int main() {
    while (scanf("%d %d", &n, &k) == 2) {
        if (n == 0 && k == 0) break;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                grid[i][j] = '.';
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i % 2 == 0) {
                    if (j % 2 == 0) {
                        generate(i, j, 'E');
                    } else {
                        generate(i, j, '.');
                    }
                } else {
                    if (j % 2 == 0) {
                        generate(i, j, '.');
                    } else {
                        generate(i, j, 'E');
                    }
                }
            }
        }
        int count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == '.') {
                    dfs(i, j);
                    count++;
                }
            }
        }
        if (count == k) {
            print_grid();
            printf("\n");
        } else {
            printf("No\n");
        }
        printf("\n");
    }
    return 0;
}
