#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

void solve() {
    int n, k;
    cin >> n >> k;

    if (n == 0 && k == 0) {
        return;
    }

    vector<string> ans(n);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if ((i + j) % 2 == 0) {
                ans[i] += 'E';
            } else {
                ans[i] += '.';
            }
        }
    }

    sort(ans.begin(), ans.end());

    int cnt = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (ans[i][j] == 'E') {
                cnt++;
            }
        }
    }

    if (cnt == k) {
        for (int i = 0; i < n; i++) {
            cout << ans[i] << endl;
        }
        cout << endl;
    } else {
        cout << "No" << endl;
    }
}

int main() {
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
