#include <iostream>
#include <vector>
#include <queue>
#include <cstring>

using namespace std;

const int dx[] = {-1, 0, 1, 0};
const int dy[] = {0, -1, 0, 1};

int H, W;
char map[100][100];
int dist[100][100];
bool visited[100][100];
int sources;

void bfs(int x, int y) {
    queue<pair<int, int>> q;
    q.push({x, y});
    dist[x][y] = 0;
    visited[x][y] = true;

    while (!q.empty()) {
        int cx = q.front().first;
        int cy = q.front().second;
        q.pop();

        for (int i = 0; i < 4; i++) {
            int nx = cx + dx[i];
            int ny = cy + dy[i];

            if (nx >= 0 && nx < H && ny >= 0 && ny < W && map[nx][ny] != '#' && !visited[nx][ny]) {
                q.push({nx, ny});
                dist[nx][ny] = dist[cx][cy] + 1;
                visited[nx][ny] = true;
            }
        }
    }
}

int main() {
    while (true) {
        cin >> H >> W;
        if (H == 0 && W == 0) break;

        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                cin >> map[i][j];
                if (map[i][j] == 'P') sources++;
            }
        }

        int minCost = 0;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (map[i][j] == 'P') {
                    memset(visited, false, sizeof(visited));
                    for (int k = 0; k < H; k++) {
                        for (int l = 0; l < W; l++) {
                            dist[k][l] = 1e9;
                        }
                    }
                    bfs(i, j);
                    for (int k = 0; k < H; k++) {
                        for (int l = 0; l < W; l++) {
                            if (map[k][l] == '*' && dist[k][l] != 1e9) {
                                minCost = max(minCost, dist[k][l]);
                            }
                        }
                    }
                }
            }
        }

        cout << minCost << endl;
    }

    return 0;
}
