from collections import deque

def bfs(grid, visited, source, city):
    queue = deque([(source, 0)])
    visited[source] = True
    while queue:
        (x, y), dist = queue.popleft()
        if grid[x][y] == '*':
            return dist
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < len(grid) and 0 <= ny < len(grid[0]) and grid[nx][ny] != '#' and not visited[(nx, ny)]:
                queue.append(((nx, ny), dist + 1))
                visited[(nx, ny)] = True
    return -1

def min_cost(grid):
    H, W = len(grid), len(grid[0])
    sources = [(i, j) for i in range(H) for j in range(W) if grid[i][j] == 'P']
    cities = [(i, j) for i in range(H) for j in range(W) if grid[i][j] == '*']
    visited = {}
    min_dist = float('inf')
    for source in sources:
        for city in cities:
            dist = bfs(grid, visited, source, city)
            if dist != -1:
                min_dist = min(min_dist, dist)
    return min_dist

def main():
    while True:
        try:
            H, W = map(int, input().split())
            grid = [list(input()) for _ in range(H)]
            print(min_cost(grid))
        except ValueError:
            break

if __name__ == "__main__":
    main()
