#include <stdio.h>
#include <math.h>

#define EPS 0.001

int ccw(double x1, double y1, double x2, double y2, double x3, double y3) {
    return (x2 - x1) * (y3 - y1) - (x3 - x1) * (y2 - y1);
}

int is_point_in_triangle(double x1, double y1, double x2, double y2, double x3, double y3, double xp, double yp) {
    int b1, b2, b3;

    b1 = ccw(x1, y1, x2, y2, xp, yp);
    b2 = ccw(x2, y2, x3, y3, xp, yp);
    b3 = ccw(x3, y3, x1, y1, xp, yp);

    return (b1 == 0 && b2 == 0 && b3 == 0) || (b1 < 0 && b2 < 0 && b3 < 0) || (b1 > 0 && b2 > 0 && b3 > 0);
}

int main() {
    double x1, y1, x2, y2, x3, y3, xp, yp;

    while (scanf("%lf %lf %lf %lf %lf %lf %lf %lf", &x1, &y1, &x2, &y2, &x3, &y3, &xp, &yp) == 8) {
        if (is_point_in_triangle(x1, y1, x2, y2, x3, y3, xp, yp))
            printf("YES\n");
        else
            printf("NO\n");
    }

    return 0;
}
