#include <stdio.h>
#include <stdlib.h>

int main() {
    int k, i, j, sum = 0;
    int *arr, *temp;

    while (scanf("%d", &k) == 1) {
        if (k == 0) {
            break;
        }

        arr = (int *)malloc(k * sizeof(int));
        temp = (int *)malloc(k * sizeof(int));

        for (i = 0; i < k * (k - 1) / 2; i++) {
            scanf("%d", &arr[i]);
        }

        for (i = 0; i < k * (k - 1) / 2; i++) {
            temp[i] = arr[i];
        }

        for (i = 0; i < k * (k - 1) / 2; i++) {
            for (j = i + 1; j < k * (k - 1) / 2; j++) {
                if (temp[i] > temp[j]) {
                    int t = temp[i];
                    temp[i] = temp[j];
                    temp[j] = t;
                }
            }
        }

        for (i = 0; i < k * (k - 1) / 2; i++) {
            sum += temp[i];
        }

        printf("%d\n", sum);

        free(arr);
        free(temp);
        sum = 0;
    }

    return 0;
}
