import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int k = scanner.nextInt();
            if (k == 0) {
                break;
            }
            int[] sales = new int[k * (k - 1) / 2];
            for (int i = 0; i < k * (k - 1) / 2; i++) {
                sales[i] = scanner.nextInt();
            }
            int[] originalSales = new int[k];
            for (int i = 0; i < k; i++) {
                originalSales[i] = scanner.nextInt();
            }
            int[] rearrangedSales = Arrays.copyOf(sales, k * (k - 1) / 2);
            Arrays.sort(rearrangedSales);
            int totalSales = 0;
            for (int i = 0; i < k; i++) {
                totalSales += originalSales[i];
            }
            for (int i = 0; i < k * (k - 1) / 2; i++) {
                totalSales += rearrangedSales[i];
            }
            System.out.println(totalSales);
        }
    }
}
