import sys

def solve():
    K = int(input())
    if K == 0:
        return
    total = 0
    nums = list(map(int, input().split()))
    for i in range(2 * K - 1):
        total += nums[i]
    print(total)

if __name__ == "__main__":
    T = 1
    for _ in range(T):
        solve()
