#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m, i, j, max_interval = 0;
    int *tl, *tr;

    while (scanf("%d %d", &n, &m) == 2) {
        if (n == 0 && m == 0) break;

        tl = (int *)malloc(n * sizeof(int));
        tr = (int *)malloc(m * sizeof(int));

        for (i = 0; i < n; i++) scanf("%d", &tl[i]);
        for (i = 0; i < m; i++) scanf("%d", &tr[i]);

        i = 0;
        j = 0;
        int prev_time = 0;

        while (i < n && j < m) {
            if (tl[i] < tr[j]) {
                max_interval = (tl[i] - prev_time > max_interval) ? tl[i] - prev_time : max_interval;
                prev_time = tl[i];
                i++;
            } else {
                max_interval = (tr[j] - prev_time > max_interval) ? tr[j] - prev_time : max_interval;
                prev_time = tr[j];
                j++;
            }
        }

        while (i < n) {
            max_interval = (tl[i] - prev_time > max_interval) ? tl[i] - prev_time : max_interval;
            prev_time = tl[i];
            i++;
        }

        while (j < m) {
            max_interval = (tr[j] - prev_time > max_interval) ? tr[j] - prev_time : max_interval;
            prev_time = tr[j];
            j++;
        }

        printf("%d\n", max_interval);

        free(tl);
        free(tr);
    }

    return 0;
}
