#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n, m;
    while (std::cin >> n >> m) {
        if (n == 0 && m == 0) break;

        std::vector<int> tl(n), tr(m);
        for (int i = 0; i < n; ++i) std::cin >> tl[i];
        for (int i = 0; i < m; ++i) std::cin >> tr[i];

        std::sort(tl.begin(), tl.end());
        std::sort(tr.begin(), tr.end());

        int i = 0, j = 0;
        int max_interval = 0;
        int prev_time = 0;

        while (i < n && j < m) {
            if (tl[i] < tr[j]) {
                max_interval = std::max(max_interval, tr[j] - prev_time);
                prev_time = tl[i];
                ++i;
            } else {
                max_interval = std::max(max_interval, tr[j] - prev_time);
                prev_time = tr[j];
                ++j;
            }
        }

        max_interval = std::max(max_interval, 1000000 - prev_time);

        std::cout << max_interval << std::endl;
    }

    return 0;
}
