#include <iostream>
#include <cmath>

struct Point {
    double x, y;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p1.x - p2.x, 2) + std::pow(p1.y - p2.y, 2));
}

double area(const Point& p1, const Point& p2, const Point& p3) {
    return std::abs((p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y)) / 2.0);
}

bool isPointInTriangle(const Point& p1, const Point& p2, const Point& p3, const Point& p) {
    double a = area(p1, p2, p3);
    double a1 = area(p, p2, p3);
    double a2 = area(p1, p, p3);
    double a3 = area(p1, p2, p);

    if (std::abs(a - (a1 + a2 + a3)) < 1e-9) {
        return true;
    }

    double d1 = distance(p, p1);
    double d2 = distance(p, p2);
    double d3 = distance(p, p3);

    if (std::abs(d1 - d2) < 1e-9 || std::abs(d2 - d3) < 1e-9 || std::abs(d3 - d1) < 1e-9) {
        return false;
    }

    return true;
}

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        Point p1, p2, p3, p;
        std::cin >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y >> p.x >> p.y;

        if (isPointInTriangle(p1, p2, p3, p)) {
            std::cout << "YES" << std::endl;
        } else {
            std::cout << "NO" << std::endl;
        }
    }

    return 0;
}
