import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            int m = scanner.nextInt();
            int[] tl = new int[n];
            int[] tr = new int[m];
            for (int i = 0; i < n; i++) {
                tl[i] = scanner.nextInt();
            }
            for (int i = 0; i < m; i++) {
                tr[i] = scanner.nextInt();
            }
            if (n == 0 && m == 0) {
                break;
            }
            int[] t = new int[n + m];
            for (int i = 0; i < n; i++) {
                t[i] = tl[i];
            }
            for (int i = 0; i < m; i++) {
                t[n + i] = tr[i];
            }
            Arrays.sort(t);
            int max = 0;
            for (int i = 1; i < n + m; i++) {
                max = Math.max(max, t[i] - t[i - 1]);
            }
            System.out.println(max);
        }
    }
}
