int main() {
    int n, i, j, k;
    int a[100];

    while (scanf("%d", &n) == 1 && n != 0) {
        for (i = 0; i < n; i++) {
            scanf("%d", &a[i]);
        }

        for (i = 0; i < n - 1; i++) {
            if (a[i + 1] == a[i] + 1) {
                if (i == 0 || a[i] != a[i - 1] + 1) {
                    printf("%d", a[i]);
                }
                if (i != n - 2) {
                    printf("-");
                }
            } else {
                if (i == 0) {
                    printf("%d", a[i]);
                } else {
                    printf(" %d", a[i]);
                }
                if (i != n - 2) {
                    printf("-");
                }
            }
        }
        if (n > 0) {
            printf(" %d", a[n - 1]);
        }
        printf("\n");
    }

    return 0;
}
