#include <iostream>
#include <vector>
#include <sstream>

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<int> pages(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> pages[i];
        }

        std::stringstream result;
        for (int i = 0; i < n; ++i) {
            if (i > 0 && pages[i] == pages[i - 1] + 1) {
                if (result.tellp() > 0) {
                    result << " ";
                }
            } else {
                if (i > 0) {
                    result << pages[i - 1];
                }
                if (i < n - 1 && pages[i] == pages[i + 1] - 1) {
                    result << "-" << pages[i + 1];
                    i++;
                } else {
                    result << pages[i];
                }
            }
        }

        std::cout << result.str() << std::endl;
    }

    return 0;
}
