#include <stdio.h>
#include <stdlib.h>

#define MAX 26

int max(int a, int b) {
    return (a > b) ? a : b;
}

int min(int a, int b) {
    return (a < b) ? a : b;
}

int dp[MAX][MAX][MAX];

int dfs(int i, int j, int k, int n) {
    if (i == n + 1) {
        return (j == 0);
    }

    if (dp[i][j][k] != -1) {
        return dp[i][j][k];
    }

    int ans = 0;

    int cap = 0;
    for (int l = i; l <= n; l++) {
        cap += min(k, dp[i][j][l]);
    }

    if (cap >= j) {
        ans = dfs(i + 1, j, k, n);
    }

    cap = 0;
    for (int l = i; l <= n; l++) {
        cap += min(k, dp[i][j][l]);
    }

    if (cap >= j) {
        ans = dfs(i + 1, j, k, n) || dfs(i, j - cap, k, n);
    }

    dp[i][j][k] = ans;

    return ans;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }

        int islands[n][2];
        for (int i = 0; i < n; i++) {
            scanf("%d %d", &islands[i][0], &islands[i][1]);
        }

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= islands[i][0]; j++) {
                for (int k = 0; k <= islands[i][0]; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }

        if (dfs(0, islands[0][0], islands[0][1], n)) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    }

    return 0;
}
