#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

bool dfs(vector<vector<int>>& dp, vector<vector<int>>& a, int i, int j, int n) {
    if (i == n - 1) return a[i][1] >= j;
    if (dp[i][j] != -1) return dp[i][j];
    bool res = false;
    for (int k = 0; k <= min(j, a[i][1]); k++) {
        res = res || dfs(dp, a, i + 1, j - k, n);
        if (res) break;
    }
    return dp[i][j] = res;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<vector<int>> a(n, vector<int>(2));
        for (int i = 0; i < n; i++) {
            cin >> a[i][0] >> a[i][1];
        }
        vector<vector<int>> dp(n, vector<int>(10000, -1));
        bool res = dfs(dp, a, 0, a[0][1], n);
        cout << (res ? "Yes" : "No") << endl;
    }
    return 0;
}
