import java.util.Arrays;
import java.util.Scanner;

public class Main {
    static class Island {
        int treasure;
        int capacity;

        public Island(int treasure, int capacity) {
            this.treasure = treasure;
            this.capacity = capacity;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;

            Island[] islands = new Island[n];
            for (int i = 0; i < n; i++) {
                islands[i] = new Island(scanner.nextInt(), scanner.nextInt());
            }

            Arrays.sort(islands, (a, b) -> a.capacity - b.capacity);

            boolean canCollect = true;
            int currentTreasure = 0;
            for (int i = 0; i < n; i++) {
                if (currentTreasure + islands[i].treasure > islands[i].capacity) {
                    canCollect = false;
                    break;
                }
                currentTreasure += islands[i].treasure;
            }

            if (canCollect && currentTreasure <= islands[0].capacity) {
                System.out.println("Yes");
            } else {
                System.out.println("No");
            }
        }
    }
}
