            double area2 = Math.abs((x1 * (yp - y3) + xp * (y3 - y2) + x2 * (y2 - yp)) / 2.0);
            double area3 = Math.abs((x1 * (y2 - yp) + xp * (yp - y3) + x3 * (y3 - y2)) / 2.0);

            if (Math.abs(area - area1 - area2 - area3) < 0.001 && area != 0) {
                System.out.println("YES");
            } else {
                System.out.println("NO");
            }
        }
    }
