#include <iostream>
#include <cmath>

double distance(double x1, double y1, double x2, double y2) {
    return std::sqrt(std::pow(x2 - x1, 2) + std::pow(y2 - y1, 2));
}

double time_of_flight(double D, double vx, double vy) {
    return D / distance(vx, vy, 0, 0);
}

double distance_traveled(double vx, double vy, double t) {
    return distance(vx, vy, vx * t, vy * t);
}

int main() {
    double D;
    while (std::cin >> D) {
        if (D == 0) break;
        double px, py, vx, vy;
        std::cin >> px >> py >> vx >> vy;
        double t = time_of_flight(D, vx, vy);
        if (t < 0) {
            std::cout << "impossible" << std::endl;
        } else {
            double x = distance_traveled(vx, vy, t);
            std::cout << x << std::endl;
        }
    }
    return 0;
}
