import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            double D = scanner.nextDouble();
            if (D == 0) break;
            double px = scanner.nextDouble();
            double py = scanner.nextDouble();
            double vx = scanner.nextDouble();
            double vy = scanner.nextDouble();
            double t = 0;
            double x = px;
            double y = py;
            while (true) {
                double r = Math.sqrt(x * x + y * y);
                if (r > D) break;
                if (r <= 1.0) {
                    double nx = -x;
                    double ny = -y;
                    double angle = Math.atan2(ny, nx);
                    double newVx = vx * Math.cos(angle) + vy * Math.sin(angle);
                    double newVy = -vx * Math.sin(angle) + vy * Math.cos(angle);
                    vx = newVx;
                    vy = newVy;
                    x = nx;
                    y = ny;
                    t += Math.sqrt(nx * nx + ny * ny) / Math.sqrt(vx * vx + vy * vy);
                } else {
                    x += vx;
                    y += vy;
                    t += Math.sqrt(vx * vx + vy * vy);
                }
            }
            if (r <= 1.0) {
                System.out.printf("%.6f%n", t);
            } else {
                System.out.println("impossible");
            }
        }
    }
}
